import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrashAlt, faPen } from "@fortawesome/free-solid-svg-icons";

const App = () => {
    const [users, setUsers] = useState([]);
    const [notification, setNotification] = useState("");
    const [create, setCreate] = useState(false);
    useEffect(() => {
        getUsers();
    }, []);

    const getUsers = () => {
        axios
            .get("https://60becf8e6035840017c17a48.mockapi.io/users")
            .then((response) => {
                setUsers(response.data);
            })
            .catch((error) => {
                console.error("Error fetching users:", error);
            });
    };

    const renderUsers = () => {
        return users.map((user) => (
            <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.name}</td>
                <td>
                    <img src={user.avatar} alt="User Avatar" />
                </td>
                <td>{user.email}</td>
                <td>{user.city}</td>
                <td>
                    <button
                        className="edit-btn"
                        onClick={() => editUser(user.id)}
                    >
                        <FontAwesomeIcon icon={faPen} />
                    </button>
                    <button
                        className="delete-btn"
                        onClick={() => deleteUser(user.id)}
                    >
                        <FontAwesomeIcon icon={faTrashAlt} />
                    </button>
                </td>
            </tr>
        ));
    };

    const createUser = (data) => {
        axios
            .post("https://60becf8e6035840017c17a48.mockapi.io/users", data)
            .then(() => {
                setNotification("User created");
                getUsers();
                clearInputFields();
            })
            .catch((error) => {
                console.error("Error creating user:", error);
            });
    };

    const handleCreate = () => {
        const name = document.querySelector('input[name="name"]').value;
        const ava = document.querySelector('input[name="ava"]').value;
        const email = document.querySelector('input[name="email"]').value;
        const city = document.querySelector('input[name="city"').value;

        const formData = {
            name: name,
            avatar: ava,
            email: email,
            city: city,
        };
        createUser(formData);
    };

    const clearInputFields = () => {
        document.querySelector('input[name="name"]').value = "";
        document.querySelector('input[name="ava"]').value = "";
        document.querySelector('input[name="email"]').value = "";
        document.querySelector('input[name="city"').value = "";
        document.querySelector('input[name="name"]').focus();
    };

    const editUser = (id) => {
        const saveBtn = document.querySelector("#edit.btn");
        axios
            .get("https://60becf8e6035840017c17a48.mockapi.io/users")
            .then(function (res) {
                return res.json();
            })
            .then((data) => {
                data.forEach((user) => {
                    if (user.id === id) {
                        document.querySelector('input[name="name"]').value =
                            user.name;
                        document.querySelector('input[name="ava"]').value =
                            user.avatar;
                        document.querySelector('input[name="email"]').value =
                            user.email;
                        document.querySelector('input[name="city"').value =
                            user.city;
                    }
                    saveBtn.onClick = () => {
                        user.name =
                            document.querySelector('input[name="name"]').value;
                        user.avatar =
                            document.querySelector('input[name="ava"]').value;
                        user.email = document.querySelector(
                            'input[name="email"]'
                        ).value;
                        user.city =
                            document.querySelector('input[name="city"').value;
                        var obj = {
                            name: user.name,
                            email: user.email,
                            avatar: user.avatar,
                            city: user.city,
                        };
                        axios
                            .put(
                                `https://60becf8e6035840017c17a48.mockapi.io/users${id}`
                            )
                            .then((response) => {
                                if (!response.ok) {
                                    throw new Error(
                                        "Network response was not ok"
                                    );
                                }
                                return response.json();
                            });
                    };
                });
            });
    };

    const deleteUser = (id) => {
        axios
            .delete(`https://60becf8e6035840017c17a48.mockapi.io/users/${id}`)
            .then(() => {
                setTimeout(function () {
                    setNotification("User deleted");
                }, 2000);
                getUsers();
            })
            .catch((error) => {
                console.error("Error deleting user:", error);
            });
    };

    return (
        <div className="App">
            <div
                id="notification-container"
                style={
                    create === true
                        ? {
                              background: "green",
                          }
                        : {
                              display: "none",
                          }
                }
            ></div>
            <div className="users w-100">
                <div className="users__add d-flex justify-content-around align-items-center">
                    <div className="users__add--name">
                        <label htmlFor="name">Name: </label>
                        <input
                            type="text"
                            name="name"
                            placeholder="Enter name here"
                        />
                    </div>

                    <div className="users__add--avatar">
                        <label htmlFor="imageURL">Avatar URL: </label>
                        <input
                            type="text"
                            name="ava"
                            id="imageURL"
                            placeholder="Paste image URL here"
                        />
                    </div>

                    <div className="users__add--email">
                        <label htmlFor="email">Email: </label>
                        <input
                            type="text"
                            name="email"
                            placeholder="Enter email here"
                        />
                    </div>

                    <div className="users__add--city">
                        <label htmlFor="city">City: </label>
                        <input
                            type="text"
                            name="city"
                            placeholder="Enter city here"
                        />
                    </div>
                    <div className="button-group">
                        <button
                            id="create"
                            className="btn"
                            onClick={() => {
                                handleCreate();
                                setCreate(!create);
                            }}
                        >
                            Create
                        </button>
                        <button id="edit" className="btn">
                            Save
                        </button>
                    </div>
                </div>

                <div id="list-users">
                    <table id="myTable">
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Name</th>
                                <th>Avatar</th>
                                <th>Email</th>
                                <th>Address</th>
                                <th>Options</th>
                            </tr>
                        </thead>
                        <tbody>{renderUsers()}</tbody>
                    </table>
                </div>
            </div>
            {notification && (
                <div className="notification-container">
                    <div className="success-message">{notification}</div>
                </div>
            )}
            <script
                src="https://kit.fontawesome.com/a39b26f8c2.js"
                crossOrigin="anonymous"
            ></script>
        </div>
    );
};

export default App;
