import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
    const [users, setUsers] = useState([]);
    const [notification, setNotification] = useState('');

    useEffect(() => {
        getUsers();
    }, []);

    const getUsers = () => {
        axios.get("https://60becf8e6035840017c17a48.mockapi.io/users")
            .then(response => {
                setUsers(response.data);
            })
            .catch(error => {
                console.error("Error fetching users:", error);
            });
    }

    const renderUsers = () => {
        return users.map(user => (
            <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.name}</td>
                <td><img src={user.avatar} alt="User Avatar" /></td>
                <td>{user.email}</td>
                <td>{user.city}</td>
                <td>
                    <button className="edit-btn" onClick={() => editUser(user.id)}>
                        <i className="fa-solid fa-pen">EDITTTT</i>
                    </button>
                    <button className="delete-btn" onClick={() => deleteUser(user.id)}>
                        <i className="fa-solid fa-trash-can">DELETE</i>
                    </button>
                </td>
            </tr>
        ));
    }

    const createUser = (data) => {
        axios.post("https://60becf8e6035840017c17a48.mockapi.io/users", data)
            .then(() => {
                setNotification("User created");
                getUsers();
                clearInputFields();
            })
            .catch(error => {
                console.error("Error creating user:", error);
            });
    }

    const handleCreate = () => {
        const name = document.querySelector('input[name="name"]').value;
        const ava = document.querySelector('input[name="ava"]').value;
        const email = document.querySelector('input[name="email"]').value;
        const city = document.querySelector('input[name="city"').value;

        const formData = {
            name: name,
            avatar: ava,
            email: email,
            city: city,
        };
        createUser(formData);
    }

    const clearInputFields = () => {
        document.querySelector('input[name="name"]').value = "";
        document.querySelector('input[name="ava"]').value = "";
        document.querySelector('input[name="email"]').value = "";
        document.querySelector('input[name="city"').value = "";
        document.querySelector('input[name="name"]').focus();
    }

    const editUser = (id) => {
        const user = users.find(user => user.id === id);
        // Your edit user logic here...
    }

    const deleteUser = (id) => {
        axios.delete(`https://60becf8e6035840017c17a48.mockapi.io/users/${id}`)
            .then(() => {
                setNotification("User deleted");
                getUsers();
            })
            .catch(error => {
                console.error("Error deleting user:", error);
            });
    }

    return (
        <div className="App">
            <div id="notification-container"></div>
            <div className="users w-100">
                <div className="users__add d-flex justify-content-around align-items-center">
                    <div className="users__add--name">
                        <label htmlFor="name">Name: </label>
                        <input
                            type="text"
                            name="name"
                            placeholder="Enter name here"
                        />
                    </div>

                    <div className="users__add--avatar">
                        <label htmlFor="imageURL">Avatar URL: </label>
                        <input
                            type="text"
                            name="ava"
                            id="imageURL"
                            placeholder="Paste image URL here"
                        />
                    </div>

                    <div className="users__add--email">
                        <label htmlFor="email">Email: </label>
                        <input
                            type="text"
                            name="email"
                            placeholder="Enter email here"
                        />
                    </div>

                    <div className="users__add--city">
                        <label htmlFor="city">City: </label>
                        <input
                            type="text"
                            name="city"
                            placeholder="Enter city here"
                        />
                    </div>
                    <button id="create" className="btn" onClick={handleCreate}>
                        Create
                    </button>
                    <button id="edit" className="btn">
                        Save
                    </button>
                </div>

                <div id="list-users">
                    <table id="myTable">
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Name</th>
                                <th>Avatar</th>
                                <th>Email</th>
                                <th>Address</th>
                                <th>Options</th>
                            </tr>
                        </thead>
                        <tbody>
                            {renderUsers()}
                        </tbody>
                    </table>
                </div>
            </div>
            {notification && (
                <div className="notification-container">
                    <div className="success-message">{notification}</div>
                </div>
            )}
            <script src="https://kit.fontawesome.com/a39b26f8c2.js" crossorigin="anonymous"></script>
            <script
                src="https://kit.fontawesome.com/a39b26f8c2.js"
                crossOrigin="anonymous"
            ></script>
        </div>
    );
}

export default App;
