import "./content.css";
import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Header from "../header/Header";
import Noti from "../notification/Noti";
import axios from "axios";

var usersApi = "https://60becf8e6035840017c17a48.mockapi.io/users";

function editUser(id) {
    axios
        .get(usersApi)
        .then((res) => res.data)
        .then((data) => {
            data.forEach((user) => {
                if (user.id === id) {
                    document.querySelector('input[name="name"]').value =
                        user.name;
                    document.querySelector('input[name="ava"]').value =
                        user.avatar;
                    document.querySelector('input[name="email"]').value =
                        user.email;
                    document.querySelector('input[name="city"').value =
                        user.city;
                    document.querySelector("#create.btn").style.display =
                        "none";
                }
                saveBtn.style.display = "block";
                saveBtn.onClick = function () {
                    user.name =
                        document.querySelector('input[name="name"]').value;
                    user.avatar =
                        document.querySelector('input[name="ava"]').value;
                    user.email = document.querySelector(
                        'input[name="email"]'
                    ).value;
                    user.city =
                        document.querySelector('input[name="city"').value;
                    var obj = {
                        name: user.name,
                        email: user.email,
                        
                    }
                };
            });
        });
}

export default function Content() {
    return (
        <>
            <div id="list-users">
                <table id="myTable">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Name</th>
                            <th>Avatar</th>
                            <th>Email</th>
                            <th>Address</th>
                            <th>Options</th>
                        </tr>
                    </thead>
                    <tbody></tbody>
                </table>
            </div>
        </>
    );
}
