import "./content.css";
import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Header from "../header/Header";
import Noti from "../notification/Noti";
import axios from "axios";
import { saveBtn } from './fileName';
var usersApi = "https://60becf8e6035840017c17a48.mockapi.io/users";


function start(){
    getUsers(render);
}

start();

function editUser(id) {
    axios
        .get(usersApi)
        .then((res) => res.data)
        .then((data) => {
            data.forEach((user) => {
                if (user.id === id) {
                    document.querySelector('input[name="name"]').value =
                        user.name;
                    document.querySelector('input[name="ava"]').value =
                        user.avatar;
                    document.querySelector('input[name="email"]').value =
                        user.email;
                    document.querySelector('input[name="city"').value =
                        user.city;
                    document.querySelector("#create.btn").style.display =
                        "none";
                }
                saveBtn.style.display = "block";
                saveBtn.onClick = function () {
                    user.name =
                        document.querySelector('input[name="name"]').value;
                    user.avatar =
                        document.querySelector('input[name="ava"]').value;
                    user.email = document.querySelector(
                        'input[name="email"]'
                    ).value;
                    user.city =
                        document.querySelector('input[name="city"').value;
                    var obj = {
                        name: user.name,
                        email: user.email,
                        avatar: user.avatar,
                        city: user.city,
                    };
                    document.querySelector('input[name="name"]').value = "";
                    document.querySelector('input[name="ava"]').value = "";
                    document.querySelector('input[name="email"]').value = "";
                    document.querySelector('input[name="city"').value = "";
                    axios
                        .put(usersApi + "/" + id, obj, {
                            headers: {
                                "Content-Type": "application/json",
                            },
                        })
                        .then(function (response) {
                            showSuccessMessage("Edited successfully");
                            return response.data;
                        })
                        .catch(function (error) {
                            console.error("Network response was not ok", error);
                        });
                };
            });
        });
}

function deleteUser(id) {
    var tableBody = document
        .getElementById("myTable")
        .getElementsByTagName("tbody")[0];
    var index;
    for (var i = 0; i < tableBody.rows.length; i++) {
        if (tableBody.rows[i].cells[0].textContent == id) {
            index = i;
            break;
        }
    }

    if (index !== undefined) {
        tableBody.deleteRow(index);
    }

    axios
        .delete(usersApi + "/" + id, {
            headers: {
                "Content-Type": "application/json",
            },
        })
        .then(function () {
            showSuccessMessage("User deleted");
            document.querySelector(".success-message").style.background = "red";
        })
        .catch(function (error) {
            console.error("Error deleting user:", error);
        });
}

function render(users) {
    var tableBody = document
        .getElementById("myTable")
        .getElementsByTagName("tbody")[0];
    tableBody.innerHTML = "";
    for (var i = 0; i < users.length; i++) {
        var row = tableBody.insertRow(i);
        var cell1 = row.insertCell(0);
        var cell2 = row.insertCell(1);
        var cell3 = row.insertCell(2);
        var cell4 = row.insertCell(3);
        var cell5 = row.insertCell(4);
        var cell6 = row.insertCell(5);
        cell1.textContent = users[i].id;
        cell2.textContent = users[i].name;
        var img = document.createElement("img");
        img.src = users[i].avatar;
        img.alt = "User Avatar";
        cell3.appendChild(img);
        cell4.textContent = users[i].email;
        cell5.textContent = users[i].city;

        var editButton = document.createElement("button");
        editButton.className = "edit-btn";
        editButton.innerHTML = '<i class="fa-solid fa-pen"></i>';
        editButton.onclick = createEditHandler(users[i].id);

        var deleteButton = document.createElement("button");
        deleteButton.className = "delete-btn";
        deleteButton.innerHTML = '<i class="fa-solid fa-trash-can"></i>';
        deleteButton.onclick = createDeleteHandler(users[i].id);

        cell6.appendChild(editButton);
        cell6.appendChild(deleteButton);

        cell1.style.textAlign = "center";
        cell2.style.textAlign = "center";
        cell3.style.textAlign = "center";
        cell4.style.textAlign = "center";
        cell5.style.textAlign = "center";
        cell6.style.textAlign = "center";
    }
}

function createEditHandler(id) {
    return function () {
        editUser(id);
    };
}

function createDeleteHandler(id) {
    return function () {
        deleteUser(id);
    };
}

function getUsers(callback) {
    fetch(usersApi)
        .then(function (respone) {
            return respone.json();
        })
        .then(callback);
}

export default function Content() {
    return (
        <>
            <div id="list-users">
                <table id="myTable">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Name</th>
                            <th>Avatar</th>
                            <th>Email</th>
                            <th>Address</th>
                            <th>Options</th>
                        </tr>
                    </thead>
                    <tbody></tbody>
                </table>
            </div>
        </>
    );
}
